ValidationRule.errorConditionFormula:
IF(
	AND(	
		RecordType.DeveloperName != 'User_Story_Bundle',
		OR(
			AND(
				ISBLANK(copado__Artifact_Repository__c), 
				(ISPICKVAL(copado__Package_Type__c, 'Unlocked') || ISPICKVAL(copado__Package_Type__c, 'Second-Generation Package'))
			),
			(NOT(ISBLANK(copado__Artifact_Repository__c)) && ISPICKVAL(copado__Package_Type__c, 'First-Generation Package') )
		)
	), true, false
)
